data.value:
// // Get Connector (conveyed items) from selected Elements // Select Element and get diagram // select s.ea_Id AS CLASSGUID, s.object_type AS CLASSTYPE, s.name As Source , d.name As Destination from t_xref x, // a lot of things like properties,.. t_connector c, t_object s, // Souce element t_object d // destination element
where x.description like '#WC##CurrentElementGUID##WC#' AND x.Behavior = 'conveyed' AND c.ea_Id = x.client
and c.ea_Id = x.client and c.start_object_id = s.object_id and c.end_object_id = d.object_id

//
// Get Elements from selected Connector (conveyed Items)
//
select  o.ea_Id AS CLASSGUID, o.object_type AS CLASSTYPE, o.name As Element
from t_object o

where  o.Object_ID in ( #ConveyedItemIDS# )

//
// Get current Item GUID (Package, Diagram, Element, Attribut, Operation)
//
// Template for macro: #CurrentItemGUID# or Alias #CurrentElementGUID#
//
// V1.00 17. May 2016 created
//

// As Element
select o.ea_Id AS CLASSGUID, o.object_type AS CLASSTYPE,o.Name AS Name,o.object_type As Type 
from t_object o
where o.ea_Id = '#CurrentElementGUID#'

UNION

// As Diagram
select dia.ea_Id AS CLASSGUID, dia.diagram_type AS CLASSTYPE,dia.Name AS Name,dia.diagram_type As Type 
from t_diagram dia
where dia.ea_Id = '#CurrentElementGUID#'

UNION

// As Package
select pkg.ea_Id AS CLASSGUID, 'Package' AS CLASSTYPE,pkg.Name AS Name,'Package' As Type 
from t_package pkg
where pkg.ea_Id = '#CurrentElementGUID#'



UNION
// As Attribute
select attr.ea_Id AS CLASSGUID,'Attribute',attr.Name AS Name,'Attribute' 
from t_attribute attr
where attr.ea_Id = '#CurrentElementGUID#'

UNION

// As operation
select opr.ea_Id AS CLASSGUID,'P',opr.Name AS Name,'Operation' 
from t_operation opr
where opr.ea_Id = '#CurrentElementGUID#'

ORDER BY Name

//
// Get current Item ID (Package, Diagram, Element, Attribut, Operation)
//
// Template for macro: #CurrentItemID# or Alias #CurrentElementID#
// Pay Attention: ID may be ambiguous. Better use #CurrentItemId#
//
// V1.00 17. May 2016 created
//

// As Element
select o.ea_Id AS CLASSGUID, o.object_type AS CLASSTYPE,o.Name AS Name,o.object_type As Type 
from t_object o
where o.object_ID = #CurrentElementID#

UNION

// As Diagram
select dia.ea_Id AS CLASSGUID, dia.diagram_type AS CLASSTYPE,dia.Name AS Name,dia.diagram_type As Type 
from t_diagram dia
where dia.diagram_ID = #CurrentElementID#

UNION

// As Package
select pkg.ea_Id AS CLASSGUID, 'Package' AS CLASSTYPE,pkg.Name AS Name,'Package' As Type 
from t_package pkg
where pkg.package_ID = #CurrentElementID#



UNION
// As Attribute
select attr.ea_Id AS CLASSGUID,'Attribute',attr.Name AS Name,'Attribute' 
from t_attribute attr
where attr.object_ID = #CurrentElementID#

UNION

// As operation
select opr.ea_Id AS CLASSGUID,'P',opr.Name AS Name,'Operation' 
from t_operation opr
where opr.object_ID = #CurrentElementID#

ORDER BY Name

//
// Template DemoRunScript
// use Script: 
// - hoDemoScript (3 parameters)
// - hoDemoPrintContext (2 parameters)
//
//
select o.ea_Id AS CLASSGUID, o.object_type AS CLASSTYPE,o.Name AS Name,o.object_type As Type 
from t_object o
where o.name like '<Search Term>#WC#' AND      o.object_type in 
     (
      "Class","Component"
      )
ORDER BY o.Name

//
// Template DiagramElements_IDS
//
select o.ea_Id AS CLASSGUID, o.object_type AS CLASSTYPE,o.Name AS Name,o.object_type As Type 
from t_object o
where o.object_ID in (#DiagramElements_IDS#)
  
ORDER BY o.Name

//
// Template DiagramElements_IDS
//
select o.ea_Id AS CLASSGUID, o.object_type AS CLASSTYPE,o.Name AS Name,o.object_type As Type 
from t_object o
where o.object_ID in (#DiagramSelectedElements_IDS#)
  
ORDER BY o.Name

//
// Template delete tree selected 
//
delete from t_object 
where ea_Id in (#TreeSelectedGUIDS#)

//
// Template Insert Element into current selected Package (direct or indirect selected via Element, Diagram, Operation, Attribute in Package)
//
insert into t_object 
   (ea_Id, object_type, Name, Package_ID)
values
   ('#NewId#', 'Class', 'XXXX', #Package#)

//
// Template Update Current selected Item (Element, Diagram, Attribute, Operation) 
//
update t_object set
   name = 'XXXClass55'
where object_ID = #CurrentItemID#
